import {
    CREATE_POST,
    //FETCH_STREAM,
    //FETCH_STREAMS,
    EDIT_POST,
    DELETE_STREAM


}from '../actions/types';

export default (state ={} ,action) =>{
    switch(action.type){
        case CREATE_STREAM:
            return {...state , [action.payload.id]: action.payload};
        case EDIT_STREAM:
            return {...state , [action.payload.id]: action.payload};
        case DELETE_STREAM:
            return _.omit(state,action.payload);
    }

}
