            {

            }

            return (
                <div>
                    <center> <h2>Edit post</h2></center>
                    <PostForm
                        initialValues={{
                            title:this.props.post.title.rendered,
                            content:this.props.post.content.rendered,
                            status:this.props.post.status
                        }}
                        onSubmit={this.onSubmit} />
                </div>
            );

        } else {
            return <div>{history.push('/')}</div>
        }
    }

    render() {

        return (
            <div>{this.renderPost()}</div>
        );
    }
}

const mapstateToProps = ({posts}, ownProps) => {
    let myDataId = ownProps.match.params.id;

    console.log(posts[myDataId],'mapstatetoprops');
    return { posts: posts[myDataId] };
};

export default connect(
    mapstateToProps,
    { editPost }
)(PostEdit);
