      return (
        <div className='right floated content'>
          <Link className='ui button primary' to={`/post/edit/${post.id}`}> Edit </Link>
          <Link className='ui button red' to={`/post/delete/${post.id}`}> Delete</Link>
        </div>
      );
    } 
  }

