import React from 'react'
import {postList,editPost} from '../../actions'
import {connect} from 'react-redux'
import PostForm from './PostForm';
import history from '../../history'


class PostEdit extends React.Component {

  onSubmit = formValues => {
    console.log()
    this.props.editPost(this.props.match.params.id,formValues);
  }

  renderData(){

    if(localStorage.getItem("validToken") && this.props.match.params.id){
      if((this.props.post === undefined) ){
        return (
         history.push('/')
        )
      }else{

      return (
        <div>
          <center><h3>Edit your post</h3></center>
          <PostForm initialValues={{
              title:this.props.post.title.rendered,
              content:this.props.post.content.rendered,
              status:this.props.post.status
            }} 
            onSubmit={this.onSubmit} 
          />
        </div>      
      )
      }
     }
      else{
        return(
          <div>
          {history.push('/')}
          </div>
        )
      }
  }
  render () {
    return(
      <div>
      {this.renderData()}
      </div>
      )
   }
}

const mapStateToProps = (state,ownProps) => {
  console.log(state.posts[ownProps.match.params.id])
  return {
    post:state.posts[ownProps.match.params.id]
  }
}
export default connect(mapStateToProps,{postList,editPost})(PostEdit)
