import React from 'react';
import {connect} from 'react-redux';
import { editPost } from '../../actions';
import PostForm from './PostForm';
import history from '../../history'
import _ from 'lodash'

class PostEdit extends React.Component {

    onSubmit = (formValues) => {
        console.log(formValues);
       this.props.editPost(this.props.match.params.id,formValues);
    };

    renderPost() {
        console.log(this.props.match.params.id)

        if(localStorage.getItem("validToken") && this.props.match.params.id && this.props.post !== undefined) {
             /* if((this.props.post === undefined) ){
                return (
                 history.push('/')
                )
            }else {  */
                console.log(this.props.posts,'render method');
                let values={
                    title:this.props.post.title.rendered,
                    content:this.props.post.content.rendered,
                    status:this.props.post.status
                }

                return (
                    <div>
                    <center> <h2>Edit post</h2></center>
                    <PostForm 
                    initialValues={ _.pick(values,'title','content','status')}
                    onSubmit ={this.onSubmit}/>
                    </div>
                );
            //}

        } else {
            return <div>{history.push('/')}</div>
        } 
    }

    render(){

        return (
            <div>{this.renderPost()}</div>
        );
    }
}

const mapstateToProps = (state ,ownProps)=>{
    //console.log(state.post[ownProps.match.params.id],'gjhghg')
   //console.log(state.post[ownProps.match.params.id],'mapstatetoprops');


    return {post : state.post[588]};
};

export default connect(
    mapstateToProps ,
    { editPost}
)(PostEdit);
