import React from 'react'
import { postList } from '../../actions';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'

class PostList extends React.Component {

  componentDidMount = (renderPostList) => {
    this.props.postList(renderPostList = (feedback) => {
      console.log(feedback);
    })
  }

  renderList(){
    return this.props.posts.map(post => {
       return (
           <div className ='item' key ={post.id}>
               {this.renderAdmin(post)}
               <i className ="large middle aligned icon profile"/>
               <div className ="content">
                   <Link to ={`/streams/${post.id}`} className = 'header'>
                       {stream.title}
                   </Link>

                   <div className ="description">
                       {post.description}
                   </div>
               </div>
           </div>
       );
   });
  }

  renderCreate(){
    if(this.props.isSignedIn){
        return(
            <div style ={{textAlign: 'right'}}>
                <Link to ="post/create" className ="ui button primary">
                    create post
                </Link>
            </div>
        );
    }

  }
  render () {
    return (
      <div>
        <center> <h3>Post List</h3></center>
        <div className ="ui celled list">{this.renderList()}</div>
         {this.renderCreate()}

      </div>
    );
  }
}

const mapStateToProps = state => {

  return { 
      posts :Object.values(state.posts),
      currentUserId : state.auth.userId,
      isSignedIn : state.auth.isSignedIn

  }; 
};


export default connect(null , { postList })(PostList)
