import React from 'react'
import { Link } from 'react-router-dom'

class Auth extends React.Component {

  state = {isSignedIn:true}

  renderAuthButton () {
    if (this.state.isSignedIn) {
      return (
          <div className="ui list">
            <Link className='ui button primary' to={`/user/login`}> Login</Link>
            <Link className='ui button primary' to={`/user/register`}> Register</Link>
          </div>
      )
    } else {
      return (
        <div className="ui list">
            <Link className='ui button primary' to={`/user/profileUpdate`}> Update Profile</Link>
            <Link className='ui button primary' to={`/user/logout`}> Logout</Link>
        </div>
      )
    }
  }

  showPostDashboard (){
    return(
      <div>
        <div className='ui secondary pointing menu'>
         <div className ='ui list'>
         <Link to ='/post/list'>veiw all post</Link>
         <Link to ='/post/create'>create your own post</Link>
         </div> 
        </div> 
      </div>
    );

  }
  render () {
    return <div>
             {this.renderAuthButton()}
           </div>
  }
}

export default Auth
