import React from 'react'
import { postList } from '../../actions';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'

class PostList extends React.Component {

  componentDidMount = () => {
    this.props.postList();
  }
  renderAdmin(post){
    //if(post.userId === this.props.currentUserId){
        return (
            <div className ='right floated content'>
                <Link to={`post/update/${post.id}`} className ='ui button primary'>Edit</Link>
                <Link to={`post/delete/${post.id}`} className ='ui button negative'>Delete</Link>    
            </div>
        );
  }

  renderList(){

    return this.props.posts.map(post => {
       return (
           <div className ='item' key ={post.id}>
               {this.renderAdmin(post)}
               <i className ="large middle aligned icon user"/>
               <div className ="content">
                   <Link to ={`/post/view/${post.id}`} className = 'header'>
                       {post.title.rendered}
                   </Link>

                   <div className ="content">
                       {post.excerpt.rendered}
                   </div>
               </div>
           </div>
       );
   });
  }

  renderCreate(){
        return(
            <div style ={{textAlign: 'right'}}>
                <Link to ="/post/create" className ="ui button primary">
                    create post
                </Link>
            </div>
        );
  }
  render () {
    return (
      <div>
        <center> <h3>Post List</h3></center>
        <div className ="ui celled list">{this.renderList()}</div>
         {this.renderCreate()}

      </div>
    );
  }
}

const mapStateToProps = state => {
    return { 
      posts :state.posts
  } 
};


export default connect(mapStateToProps , { postList })(PostList)
