import React from 'react';
import {connect} from 'react-redux';
import {postList ,editPost } from '../../actions';
import PostForm from './PostForm';
import history from '../../history'
import _ from 'lodash'

class PostEdit extends React.Component {

    /*componentWillMount(){

        this.props.postList(this.props.match.params.id);
    }
   */
    onSubmit = (formValues) => {
        console.log(formValues);
       this.props.editPost(this.props.match.params.id,formValues);
    };

    renderEditContent(){
        if(!this.props.post){
            return <div>{history.push('/')}</div>
        }
    return (
        <div>
            <h3>Edit a stream</h3>
            <PostForm 
                initialValues ={_.pick(this.props.stream, 'title' ,'description')} 

                onSubmit = {this.onSubmit}   
            />
        </div>
    );
    }

    render(){
         return <div>{this.renderEditContent}</div>   
    }

}

const mapstateToProps = (state ,ownProps)=>{

    return {post : state.posts[ownProps.match.params.id]};
};

export default connect(
    mapstateToProps ,
    { postList ,editPost}
)(PostEdit);
