import {
    CREATE_POST,
    //FETCH_STREAM,
    //FETCH_STREAMS,
    EDIT_POST,
    //DELETE_POST


}from '../actions/types';

export default (state ={} ,action) =>{
    switch(action.type){
        case CREATE_POST:
            return {...state , [action.payload.id]: action.payload};
        case EDIT_POST:
            return {...state , [action.payload.id]: action.payload};
        //case DELETE_POST:
          //  return _.omit(state,action.payload);

        default :
            return state;
    }

}
