import React from 'react';
import {connect} from 'react-redux';
import { editPost } from '../../actions';
import PostForm from './PostForm';
import history from '../../history'

class PostEdit extends React.Component {

    onSubmit = (formValues) => {
        console.log(formValues);
       this.props.editPost(this.props.match.params.id,formValues);
    };

    EditContent(){
        if(!this.props.post){
            return <div>{history.pushState('/')}</div>
        }
        else {
            return (
                <div>
                    <center><h2>Edit a stream</h2></center>
                        <PostForm 
                            initialValues={{
                                title:this.props.post.title.rendered,
                                content:this.props.post.content.rendered,
                                status:this.props.post.status
                            }} 
                            onSubmit = {this.onSubmit}   
                        />
                </div>
            );
        }


    }

    render(){
         return <div>{this.EditContent}</div>   
    }

}

const mapstateToProps = (state ,ownProps)=>{

    return {post : state.posts[ownProps.match.params.id]};
};

export default connect(
    mapstateToProps ,
    { editPost}
)(PostEdit);
