import  axios from '../apis/axios'
import history from "../history"
import {toastr} from 'react-redux-toastr'

const header = {
  "Content-Type" : "application/json"
}

const toastrSuccessFunction = (title,msg) => toastr.success(title,msg)
const toastrWarningFunction = (title,msg) => toastr.error(title,msg) 

export const signIn = formValues => async () => {
    try{
      const response  = await axios.post('jwt-auth/v1/token',{...formValues},{header:header});
      console.log(response.data)

      toastrSuccessFunction(`SignIn Successful`,`${formValues.username}`)
      localStorage.setItem("authToken", response.data.token);

      //console.log(localStorage.getItem("authToken"))
      history.push('/'); 
    }
    catch(error){
      toastrWarningFunction(`Form Submission Error`,`${formValues.username}`);
    }     
  };

  export const signUp = formValues => async () => {
    try{
      const response  = await axios.post('wp/v2/users/register',{...formValues});
      console.log(response.data)
      toastrSuccessFunction(`${response.data.message}`,`${formValues.username}`)
      history.push('/'); 
    }
    catch(error){
      toastrWarningFunction(`Form Submission Error`,`${formValues.username}`);
    }   
  };

  export const updateUser = formValues => async () => {
    //console.log(formValues);
    const response  = await axios.post('/wp/v2/users/register',{...formValues});
    console.log(response.data.message);
  };

  export const postList = (renderPostList) => async (dispatch) => {
    const response =await axios.get('/wp/v2/posts');
    console.log(response.data);
    dispatch ({type: POST ,payload :response.data});
    renderPostList(response);

  };

  export const postCreate = formValues => async () => {
    //console.log(formValues);
    const response  = await axios.post('/wp/v2/users/register',{...formValues});
    console.log(response.data.message); 
  };
